import 'package:firebase_core/firebase_core.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_icons/flutter_icons.dart';import 'package:parkink_app/models/UserModel.dart';import 'package:parkink_app/screens/connected_screen/connected_screen.dart';import 'package:parkink_app/screens/loadScreen/Loading.dart';import 'package:parkink_app/screens/wrapper.dart';import 'package:parkink_app/services/auth.dart';import 'package:provider/provider.dart';void main() {  WidgetsFlutterBinding.ensureInitialized();  runApp(App());}class App extends StatelessWidget {  // Create the initialization Future outside of `build`:  final Future<FirebaseApp> _initialization = Firebase.initializeApp();  @override  Widget build(BuildContext context) {    return FutureBuilder(      // Initialize FlutterFire:      future: _initialization,      builder: (context, snapshot) {        // Check for errors        if (snapshot.hasError) {          return MaterialApp(            home: Connected(),          );        }        // Once complete, show your application        if (snapshot.connectionState == ConnectionState.done) {          return ParkingApp();        }        // Otherwise, show something whilst waiting for initialization to complete        return MaterialApp(          home: Loading(),        );      },    );  }}class ParkingApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return StreamProvider<UserModel>.value(      value: AuthService().currentUser,      child: MaterialApp(        debugShowCheckedModeBanner: false,        title: "Parking Application",        theme: ThemeData(          primaryColor: Color.fromRGBO(50, 65, 85, 1)        ),        home: Wrapper(),      ),    )    ;  }}